import React, {useState} from 'react'
import {fetchWeather} from './fetchWeather'
import 'remixicon/fonts/remixicon.css'
import './App.css'

function App() {
  const [lat, setLat] = useState('')
  const [lon, setLon] = useState('')
  const [meteo, setMeteo] = useState({})
  console.log(meteo)

  const search = async(e) => {
      const data = await fetchWeather(lat, lon)
      setMeteo(data)
      setLat('')
      setLon('')
  }

  return (
    <div className="main-container">
      <div className="inputs">
        <input type="text" className="search" placeholder="Enter latitude..." value={lat} onChange={(e) => setLat(e.target.value)} />
        <input type="text" className="search" placeholder="Enter longitude..." value={lon} onChange={(e) => setLon(e.target.value)} />
        <button onClick={search} className="search">Search</button>
      </div>
      {meteo.main && (
        <div className="city">
          <h2 className="city-name">
          <span>{meteo.lat}</span>
          <sup>{meteo.lon}</sup>
          </h2>
          <div className="city-temp">
              {Math.round(meteo.main.temp)}
              <sup>&deg;C</sup>
          </div>
          <div className="info">
            <img className="city-icon" src={`https://openweathermap.org/img/wn/${meteo.weather[0].icon}@2x.png`} alt={meteo.weather[0].description}/>
            <p>{meteo.weather[0].description}</p>
            <p>lattude: {meteo.coord.lat}</p>
            <p>longitude: {meteo.coord.lon}</p>
          </div>
        </div>
      )}
    </div>
  )
}

export default App;
